package arrayBasics;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=4;
int b=9;
a=a+b;// a=a*b;
b=a-b;// b=a/b;
a=a-b;// a=a/b;
System.out.println(a);
System.out.println(b);
int x=3;
int y=2;
int temp = 0;
temp=x;
x=y;
y=temp;
System.out.println(x);
System.out.println(y);
	}

}
