package operators;

public class BitwiseNOT {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=3;// 0011
		int y=5;// 0101
		int z=~y; // BITWISE NOT
		System.out.println(z);// NEGATIVE VALUE OF Y+1;

	}

}



// BITWISE "NOT" OPERATOR IS PERFORMED FOR ONLY "ONE OPERAND"...
// NOT FOR TWO OPERANDS LIKE OTHER BITWISE OPERATORS